import React from 'react'
import Axios from 'axios'

import { useSelector, useDispatch } from 'react-redux'
import { Table, Button, Form } from 'react-bootstrap'
import {
    editCategory,
    deleteCategory
} from '../actions';

const GetCategory = () => {
    const [data, setData] = React.useState([])
    const [editIndex, setEditIndex] = React.useState(null)

    const dispatch = useDispatch();

    React.useEffect(() => {
        Axios.get(`http://localhost:2000/admin/getcategory`)
            .then(res => (setData(res.data)))
            .catch(err => console.log(err))
    }, [data])

    const deleteHandler = (itemId) => {
        const input = {
            id: itemId,
        }
        dispatch(deleteCategory(input))
        console.log(input)
    }

    const saveHandler = (itemId) => {
        const num = data[0].order_number
        const price = data[editIndex].price
        const input = {
            id_product: itemId,
            order_number: num,
            qty,
            total: price * qty
        }
        dispatch(editCategory(input, id))
        setEditIndex(null)
        console.log(input)
    }

    const renderTable = () => {
        return data.map((item, index) => {
            return (
                index === editIndex
                    ?
                    <tr key={index}>
                        <td style={{ textAlign: 'center' }}>{index + 1}</td>
                        <td style={{ textAlign: 'right' }}>
                            <Form.Control style={{ width: '90px', fontSize: '20px' }} />
                        </td>
                        <td style={{ textAlign: 'center' }}>{item.id}</td>
                        <td style={{ textAlign: 'center' }}>
                            <Button variant="outline-success" style={{ marginRight: '5px' }} > ✔ </Button>
                            <Button variant="outline-danger" style={{ marginLeft: '5px' }} onClick={() => setEditIndex(null)}> ❌ </Button>
                        </td>
                    </tr>
                    :
                    <tr key={index}>
                        <td style={{ textAlign: 'center' }}>{index + 1}</td>
                        <td>{item.category}</td>
                        <td style={{ textAlign: 'center' }}>{item.id}</td>
                        <td style={{ textAlign: 'center' }}>
                            <Button variant="warning" style={{ marginRight: '5px' }} onClick={() => setEditIndex(index)}> Edit </Button>
                            <Button variant="danger" style={{ marginLeft: '5px' }} onClick={() => deleteHandler(item.id)}> Delete </Button>
                        </td>
                    </tr>
            )
        })
    }
    return (
        <div style={{ marginTop: "138px" }}>
            <Form.Control style={{ width: '250px', fontSize: '20px' }} />
            <Button variant="outline-info">➕</Button>
            <Table striped bordered hover variant="dark">
                <thead style={{ backgroundColor: '#2f3640', textAlign: 'center' }}>
                    <tr style={{ fontFamily: 'Roboto, sans-serif' }}>
                        <th>No</th>
                        <th>Category</th>
                        <th>ID</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>{renderTable()}</tbody>
            </Table>
        </div>
    )
}

export default GetCategory
