import React from 'react'
import Axios from 'axios'
import { Redirect, Link } from 'react-router-dom'

import { useSelector, useDispatch } from 'react-redux'
import { Table, Button, Form, Modal, Image, Alert } from 'react-bootstrap'

const GetAll = () => {
    const [data, setData] = React.useState([])
    const [editIndex, setEditIndex] = React.useState(null)

    const { products } = useSelector((state) => {
        return {
            products: state.product.products
        }
    })

    const dispatch = useDispatch();

    React.useEffect(() => {
        Axios.get(`http://localhost:2000/admin/getall`)
            .then(res => (setData(res.data)))
            .catch(err => console.log(err))
    }, [data])

    const renderTable = () => {
        return data.map((item, index) => {
            return (
                index === editIndex
                    ?
                    <tr key={index}>
                        <td style={{ textAlign: 'center' }}>{index + 1}</td>
                        <td>
                            <Image style={{ width: 60, height: 60, marginRight: "15px" }} src={item.image} rounded />
                            {item.name}
                        </td>
                        <td style={{ textAlign: 'right' }}>{item.price}</td>
                        <td style={{ textAlign: 'center' }}>
                            <div style={{ display: 'flex' }}>
                                <button
                                    // disabled={qty <= 0 ? true : false}
                                    // onClick={() => setQty(qty - 1)}
                                    style={{ height: "2rem", margin: "10px", borderRadius: "30px" }}
                                ><i className="fas fa-minus"></i></button>
                                {/* <Form.Control style={{ width: '90px', fontSize: '20px' }} onChange={(e) => changeQty(e)} value={qty} min={0} /> */}
                                <button
                                    // disabled={qty >= products.total_stock ? true : false}
                                    // onClick={() => setQty(qty + 1)}
                                    style={{ height: "2rem", margin: "10px", borderRadius: "30px" }}
                                ><i className="fas fa-plus"></i></button>
                            </div>
                        </td>
                        <td style={{ textAlign: 'right' }}>{item.price}</td>
                        <td style={{ textAlign: 'center' }}>
                            <Button variant="outline-success" style={{ marginRight: '5px' }} > ✔ </Button>
                            <Button variant="outline-danger" style={{ marginLeft: '5px' }}> ❌ </Button>
                        </td>
                    </tr>
                    :
                    <tr key={index}>
                        <td style={{ textAlign: 'center' }}>{index + 1}</td>
                        <td>
                            <Image style={{ width: 60, height: 60, marginRight: "15px" }} src={item.image} rounded />
                            {item.name}
                        </td>
                        <td style={{ textAlign: 'right' }}>IDR {item.price}</td>
                        <td style={{ textAlign: 'right' }}>IDR {item.price}</td>
                        <td style={{ textAlign: 'center' }}>
                            <Button variant="warning" style={{ marginRight: '5px' }} > Edit </Button>
                            <Button variant="danger" style={{ marginLeft: '5px' }} > Delete </Button>
                        </td>
                    </tr>
            )
        })
    }
    return (
        <div style={{ marginTop: "138px" }}>
            <Table striped bordered hover variant="dark">
                <thead style={{ backgroundColor: '#2f3640', textAlign: 'center' }}>
                    <tr style={{fontFamily:'Roboto, sans-serif'}}>
                        <th>No</th>
                        <th>Product</th>
                        <th>Price</th>
                        <th>Total Stock</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>{renderTable()}</tbody>
            </Table>
        </div>
    )
}

const styles = {
    container: {
        marginTop: "138px",
        padding: "0 20px",
        fontFamily: "PT Serif",
        paddingBottom: '30px'
    },
    content: {
        display: "flex",
        height: "80vh"
    },
    left: {
        display: "flex",
        flexBasis: "40%",
        justifyContent: "center",
        alignItems: "center",
    },
    right: {
        display: "flex",
        flexDirection: "column",
        flexBasis: "60%",
        justifyContent: "space-between",
        padding: "0 20px"
    },
    description: {
        fontSize: "13px",
        fontWeight: "350",
    },
    size: {
        display: "flex",
        justifyContent: "space-between"
    },
    qty: {
        display: "flex",
        width: "30%",
    },
    buttonQty: {
        display: "flex",
        flexDirection: "column",
        justifyContent: "center"
    },
    checkout: {
        width: "20%",
        height: "8%",
        backgroundColor: "#118ab2",
        marginLeft: 550
    }
}

export default GetAll
