import React from 'react'
import Axios from 'axios'
import { connect } from 'react-redux'
import { Redirect, Link } from 'react-router-dom'

import { login } from '../actions'

import { useSelector } from 'react-redux'
import { Table, Button, Form, Modal, Image } from 'react-bootstrap'

const CartPage = () => {
    const [data, setCart] = React.useState([])

    const { products } = useSelector((state) => {
        return{
            products: state.product.products
        }
    })

    const { id } = useSelector((state) => {
        return {
            id: state.user.id_user
        }
    })


    React.useEffect(() => {
        Axios.get(`http://localhost:2000/cart/get/${id}`)
            .then(res => (setCart(res.data)))
            .catch(err => console.log(err))
    }, [])

    const renderTable = () => {
        return cart.map((item, index) => {
          return (
            <tr>
              <td>{index + 1}</td>
              <td>{products.name}</td>
              <td>{products.images}</td>
              <td>{products.price}</td>
              <td>{products.company.name}</td>
            </tr>
          )
        })
    }


    return (
        <div style={{ marginTop: "100px" }}>
            <Table striped bordered hover variant="dark">
                <thead style={{ backgroundColor: '#2f3640', textAlign: 'center' }}>
                    <tr>
                        <th>No</th>
                        <th>Name</th>
                        <th>Image</th>
                        <th>Price</th>
                        <th>Quantity</th>
                        <th>Total</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody> {renderTable()} </tbody>
            </Table>
        </div>
    )
}

export default CartPage
